    private static byte[] convertirStringToByte(String str) {
        int j;
        byte[] data = new byte[str.length() / 2];
        for (j = 0; j < str.length(); j += 2) {
            data[j / 2] = (byte) ((Character.digit(str.charAt(j), 16) << 4)
                    + Character.digit(str.charAt(j + 1), 16));
        }
        return data;
    }

